mapper.select:
SELECT board_id AS boardId, category_id AS categoryId, writer, password, title, content, views, created_date as generationTimestamp, modified_date AS modificationTimestamp FROM board WHERE created_date >= #{startDate} AND created_date <= #{endDate}
AND (title LIKE CONCAT('%', #{search}, '%') OR writer LIKE CONCAT('%', #{search}, '%') OR content LIKE CONCAT('%', #{search}, '%'))
AND category_id = (SELECT category_id FROM category WHERE name = #{category})
ORDER BY created_date DESC LIMIT #{limit} OFFSET #{offset}

SELECT count(*) FROM board



        created_date >= #{startDate}




        AND created_date <= #{endDate}



        AND (title LIKE CONCAT('%', #{search}, '%') OR
        writer LIKE CONCAT('%', #{search}, '%') OR
        content LIKE CONCAT('%', #{search}, '%'))



            AND category_id = (SELECT category_id FROM category WHERE name = #{category})



LIMIT #{limit} OFFSET #{offset}

SELECT name, category_id AS categoryID
FROM category
WHERE category_id = #{categoryId}

mapper.update:
UPDATE board
SET views = views + 1
WHERE board_id = #{boardId}
